import React from 'react';
import "./style.css"
import RepeatIcon from '@mui/icons-material/Repeat';
import SkipNextIcon from '@mui/icons-material/SkipNext';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import ShuffleIcon from '@mui/icons-material/Shuffle';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import { Grid, Slider } from '@mui/material';
import { useDataLayerValue } from '../DataLayer';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from './FavoriteIcon';

const Footer = () => {
    // const [{songPlaying, discover_weekly}, dispatch] = useDataLayerValue()
    // const songList = discover_weekly.tracks.items
    // const _songPlaying = songList.find(item => item.track.id === songPlaying)
    // console.log("song playing data", _songPlaying)
    const sliderChange = (e,val) => {
        console.log("hmmm",val)
    }
    return (
        <div className='footer'>
            <div className="footer_left">
                {/* <img src={_songPlaying.track.album.images[0].url}
                className='song_photo' alt="" />
                <div className="songInto p-10">
                    <h3 className="song_name">{_songPlaying.track.name}</h3>
                    <p className="artist">
                        {_songPlaying.track.artists.map(item =>item.name).join(", ")}
                    </p> */}
                    <FavoriteIcon></FavoriteIcon>
            </div>
            <div className="footer_center">
                <ShuffleIcon/>
                <SkipPreviousIcon/>
                <PlayArrowIcon/>
                <SkipNextIcon/>
                <RepeatIcon/>
            </div>
            <div className="footer_right">
                <VolumeUpIcon/>
                <div className="slider">
                    <Slider 
                        onChange={(e,val) => sliderChange(e,val)}
                        size="small"
                        defaultValue={10}
                        aria-label="Small" 
                        className="volume-slideBar p-10"  
                        color="secondary"  
                    />
                </div>
                        
                <Grid/>
            </div>
            
        </div>
    );
};

export default Footer;
