import React from 'react';
import { useDataLayerValue } from '../DataLayer';
import Header from './Header';
import SongRow from './SongRow';
import "./style.css"
const Body = ({spotify}) => {
    const [{discover_weekly}, dispatch] = useDataLayerValue()

    const playSong = (id) => {
        spotify
          .play({
            uris: [`spotify:track:${id}`],
          })
          .then((res) => {
            spotify.getMyCurrentPlayingTrack().then((r) => {
              dispatch({
                type: "SET_ITEM",
                item: r.item,
              });
              dispatch({
                type: "SET_PLAYING",
                playing: true,
              });
            });
          });
      };
    return (
        <div className='body'>
            <Header/>
            <div className="">
                <div className=""></div>
            </div>
            {console.log("ðŸ‘€ðŸ‘€",discover_weekly)}
            <div className="songList">
            {discover_weekly?.tracks.items.map(item =>                
                   ( <SongRow playSong={playSong} songInfo = {item}/>)

            )}
            </div>
        </div>
    );
};

export default Body;
