    user: null,
    token: null,
    playlist: []
}
const reducer = (state, action) => {
    console.log("action ne baa ðŸ¤ž:",action)
    switch (action.type){ 
        case "changeUserData":
            return {
                ...state,
                user: action.user
            }
        case "changeToken":
            return{
                ...state,
                token: action.token
            }
        case "changePlaylist":
            return{
                ...state,
                playlist: <action className="playlis"></action>
            }    
        default:
