export const initialState ={
    user: null,
    token: null,
    playlist: [],
    discover_weekly: null,
    songPlaying: null
}
const reducer = (state, action) => {
    console.log("action ne baa ðŸ¤ž:",action)
    switch (action.type){ 
        case "changeUserData":
            return {
                ...state,
                user: action.user
            }
        case "changeToken":
            return{
                ...state,
                token: action.token
            }
        case "changePlaylist":
            return{
                ...state,
                playlist: action.playlist
            }    
        case "changeDiscover":
            return{
                ...state,
                discover_weekly: action.discover_weekly
            }
        case "changeSongPlaying":
            return{
                ...state,
                songPlaying: action.songPlaying
            }
