export const initialState ={
    user: null,
    token: '81d10886764c475c8b95ecff05c77771'
}
const reducer = (state, action) => {
    console.log("action ne baa ðŸ¤ž:",action)
    switch (action.type){ 
        case "changeUserData":
            return {
                ...state,
                user: action.user
            }
        case "changeToken":
            return{
                ...state,
                token: action.token
            }
        default:
            return state
    }
}
export default reducer
