const s = new SpotifyWebApi();

function App() {
  const [{ token }, dispatch] = useDataLayerValue();

  useEffect(() => {

      fetch(`http://localhost:9000/song`)
        .then((res) => res.json())
        .then((jsonResp) => {
          dispatch({
            action: 'setAppData',
            appData: jsonResp
          })
          console.log("ket qua sau khi fetch ve:",{jsonResp})
        })
        .catch((error) => console.log("loi ne ba:" + error));
    const hash = getTokenFromResponse();
    console.log("###",hash)
    window.location.hash = "";
    let _token = hash.access_token;

    if (_token) {
      s.setAccessToken(_token);

      dispatch({
        type: "SET_TOKEN",
        token: _token,
      });

      s.getPlaylist("37i9dQZEVXcJZyENOWUFo7").then((response) =>
     { console.log("🐱‍🏍🐱‍🏍🐱‍🏍",response)
        dispatch({
          type: "SET_DISCOVER_WEEKLY",
          discover_weekly: response,
        })} 
      )

      s.getMyTopArtists().then((response) =>
        dispatch({
          type: "SET_TOP_ARTISTS",
          top_artists: response,
        })
      );

      dispatch({
        type: "SET_SPOTIFY",
        spotify: s,
      });

      s.getMe().then((user) => {
        dispatch({
          type: "SET_USER",
          user,
        });
      });

      s.getUserPlaylists().then((playlists) => {
        dispatch({
          type: "SET_PLAYLISTS",
          playlists,
        });
      });
    }
  }, );

  return (
    <div className="app">
    {token? console.log("App mouted player",):console.log("App mouted login")}
      {!token && <Login />}
      {token && <Player spotify={s} />}
    </div>
  );
}

export default App;
